var TableDatatablesAjax = function(  ) {

    var a = function() {
            $(".date-picker").datepicker({
                rtl: App.isRTL(),
                autoclose: !0
            })
        },

        e = function( datatableId, urlLink ) {

            var a = new Datatable;
            a.init({
                src: $("#"+ datatableId ),
                onSuccess: function(a, e) {},
                onError: function(a) {},
                onDataLoad: function(a) {},
                loadingMessage: "Loading...",
                dataTable: {
                    bStateSave: !0,
                    lengthMenu: [
                        [10, 20, 50, 100, 150, -1],
                        [10, 20, 50, 100, 150, "All"]
                    ],
                    pageLength: 10,
                    ajax: {
                        url: urlLink
                    },
                    order: [
                        [1, "desc"]
                    ]
                }
            }), a.getTableWrapper().on("click", ".table-group-action-submit", function(e) {
                e.preventDefault();
                var t = $(".table-group-action-input", a.getTableWrapper());

                if( t.val() == '9'){
                    var ans = confirm( "Are you sure you want to delete?" );
                    if( ans ){
                        "" != t.val() && a.getSelectedRowsCount() > 0 ? (a.setAjaxParam("customActionType", "group_action"), a.setAjaxParam("customActionName", t.val()), a.setAjaxParam("id", a.getSelectedRows()), a.getDataTable().ajax.reload(), a.clearAjaxParams()) : "" == t.val() ? App.alert({
                            type: "danger",
                            icon: "warning",
                            message: "Please select an action",
                            container: a.getTableWrapper(),
                            place: "prepend"
                        }) : 0 === a.getSelectedRowsCount() && App.alert({
                            type: "danger",
                            icon: "warning",
                            message: "No record selected",
                            container: a.getTableWrapper(),
                            place: "prepend"
                        })
                    }
                }else{
                    "" != t.val() && a.getSelectedRowsCount() > 0 ? (a.setAjaxParam("customActionType", "group_action"), a.setAjaxParam("customActionName", t.val()), a.setAjaxParam("id", a.getSelectedRows()), a.getDataTable().ajax.reload(), a.clearAjaxParams()) : "" == t.val() ? App.alert({
                        type: "danger",
                        icon: "warning",
                        message: "Please select an action",
                        container: a.getTableWrapper(),
                        place: "prepend"
                    }) : 0 === a.getSelectedRowsCount() && App.alert({
                        type: "danger",
                        icon: "warning",
                        message: "No record selected",
                        container: a.getTableWrapper(),
                        place: "prepend"
                    })
                }

            })
        };
    return {
        init: function( id, urlLink ) {
            a(), e( id, urlLink )
        }
    }
}();
