var Login = function() {
    i = function() {
        var e = $("#form_sample_3"),
            r = $(".alert-danger", e),
            i = $(".alert-success", e);
        e.on("submit", function() {
            //  for (var e in CKEDITOR.instances) CKEDITOR.instances[e].updateElement()
        }), e.validate({
            errorElement: "span",
            errorClass: "help-block help-block-error",
            focusInvalid: 1,
            ignore: "",
            rules: {
            },
            messages: {

            },
            errorPlacement: function(e, r) {

                r.parent(".input-group").size() > 0 ? e.insertAfter(r.parent(".input-group")) : r.attr("data-error-container") ? e.appendTo(r.attr("data-error-container")) : r.parents(".radio-list").size() > 0 ? e.appendTo(r.parents(".radio-list").attr("data-error-container")) : r.parents(".radio-inline").size() > 0 ? e.appendTo(r.parents(".radio-inline").attr("data-error-container")) : r.parents(".checkbox-list").size() > 0 ? e.appendTo(r.parents(".checkbox-list").attr("data-error-container")) : r.parents(".checkbox-inline").size() > 0 ? e.appendTo(r.parents(".checkbox-inline").attr("data-error-container")) : e.insertAfter(r.parent())
                var i = $(r).parent(".input-icon").children("i");
                i.removeClass("fa-check").addClass("fa-warning"), i.attr("data-original-title", e.text()).tooltip({
                    container: "body"
                })
            },
            invalidHandler: function(e, t) {
                i.hide(), r.show(), App.scrollTo(r, -200)
            },
            highlight: function(e) {
                //$(e).closest(".form-group").addClass("has-error")
                $(e).closest(".form-group").removeClass("has-success").addClass("has-error")
            },
            unhighlight: function(e) {
                // $(e).closest(".form-group").removeClass("has-error")
            },
            success: function(e, r) {

                //  e.closest(".form-group").removeClass("has-error")
                var i = $(r).parent(".input-icon").children("i");
                $(r).closest(".form-group").removeClass("has-error").addClass("has-success"), i.removeClass("fa-warning").addClass("fa-check")
            },
            submitHandler: function(e) {
                i.show(), r.hide(), e[0].submit()
            }
        }), $(".select2me", e).change(function() {
            e.validate().element($(this))
        })
    },
        t = function() {
            jQuery().wysihtml5 && $(".wysihtml5").size() > 0 && $(".wysihtml5").wysihtml5({
                stylesheets: ["../bootstrap-wysihtml5/wysiwyg-color.css"]
            })
        };
    return {
        init: function() {
            i()
        }
    }
}();
jQuery(document).ready(function() {
    Login.init()
});